sp_widget.client_script:
function fitbitCtrl($http) {
	/* widget controller */
	var c = this;
	console.log(c.data.userActivity);
	if(c.data.userActivity.summary){
		c.data.steps = numberWithCommas(c.data.userActivity.summary.steps);
	}

	function numberWithCommas(x) {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}


}

